import { addons } from '@storybook/manager-api';
import { create } from '@storybook/theming/create';

// Logo as base64 data URI
const logoBase64 = 'data:image/svg+xml;base64,';

const dracoTheme = create({
  base: 'dark',
  brandTitle: 'Draco UI React',
  brandUrl: 'https://github.com/draco-ui/dracoui',
  brandImage: logoBase64,
  brandTarget: '_self',
});

addons.setConfig({
  theme: dracoTheme,
  enableShortcuts: false,
  sidebar: {
    showRoots: false,
    collapsedRoots: [],
  },
  toolbar: {
    title: { hidden: false },
    zoom: { hidden: false },
    eject: { hidden: false },
    copy: { hidden: false },
    fullscreen: { hidden: false },
  },
});
